import React, { useCallback, useContext, useEffect, useState } from 'react';
import './App.css';
import Board from './components/board'
import dice from './data'
import PrimarySearchAppBar from './components/myAppBar'
import { Paper, Button, ThemeProvider, Typography, InputBase } from '@material-ui/core'
import { withStyles } from '@material-ui/core/styles';
import theme from './theme';
import Ui from './components/ui'
import Store from "./store/store";
import {Context} from "./store/store"
import reducer from "./store/reducer"

//import <link rel="stylesheet" href="https://fonts.googleapis.com/css?family=Roboto:300,400,500,700&display=swap" />

const initialGlobalState = {
  isSettingsDialogOpen: false,
  rowsNum: 5,
  colsNum: 5,
  match: 0,
  totalTime: 10,
  countDown: 2
};

export const GlobalStateContext = React.createContext(initialGlobalState);
export const DispatchStateContext = React.createContext(undefined);


/**
 * Global State provider & hooks
 */
const GlobalStateProvider = ({ children }) => {
  const [globalState, globalDispatch] = React.useReducer(
    reducer,
    initialGlobalState
  );
  return (
    <GlobalStateContext.Provider value={globalState}>
      <DispatchStateContext.Provider value={globalDispatch}>
        {children}
      </DispatchStateContext.Provider>
    </GlobalStateContext.Provider>
  );
};

const useGlobalState = () => [
  React.useContext(GlobalStateContext),
  React.useContext(DispatchStateContext)
];

const App = React.memo(() => {

