import React, { Fragment } from 'react';
import Headers from './headers'
import Row from './row'
console.log('Board')

function Board({ rolls, colsNum }) {

    console.log(`printing board with ${rolls.length} rolls in ${colsNum} columns`)
    console.log('rolls ', rolls)
    let rows = []
    let rowRolls = []
    for (let [index, roll] of rolls.entries()) {
            rowRolls.push({
                x:(colsNum/index-colNums),
                y:(index*colsNum/colNums),
                content: rolls[roll]
            })

            if(rowRolls.length === colsNum) 
            {   
                console.log(`pushing rowRolls row_${colsNum/index-colNums} into the board`)
                rows.push(<tr key={`row_${colsNum/index-colNums}`.toString()}><Row key={`row_${colsNum/index-colNums}`.toString()} id={colsNum/index-colNums} rowRolls={rowRolls}/></tr>)
                rowRolls = []
            } 
        }
     return (rows)
}
export default Board
