import React, { Component, Fragment } from 'react';
import './App.css';
import Board from './components/board'
import Die from './components/die'
import dice from './data'

//import <link rel="stylesheet" href="https://fonts.googleapis.com/css?family=Roboto:300,400,500,700&display=swap" />


class App extends Component{

  constructor(props) {
    super(props);
    this.state = {
        dice: dice,
        rolls: [],
        rowsNum: 5,
        colsNum: 5
    }
    this.handleEvent = this.handleEvent.bind(this);
}

handleEvent(event, id) {
  event.stopPropagation();
  switch (event.type) {
      case 'click':
          console.log('clicked ', event.target, id);
          this.rollsDice()
          break
      default:
          console.log('unhandled ', event.type)
  }
}

rollsDice() { 
  //clona l'array dice in rolls 
  let rolls = []
  dice.map(die => {
      const roll = Math.floor( Math.random() * 6 )
      console.log('roll', roll)
      console.log('die', die)
      console.log(die[roll])
      rolls.push(die[roll])
  })
  console.log('rolls ', rolls)
  this.setState({'rolls':rolls})
  console.log('state rolls ', this.state.rolls)
}

  render() {
    return (
      <Fragment>
        <div>
            <button key='1' onClick={(e, key) => this.handleEvent(e, key)}>
                    Roll dice
            </button>
            {(this.state.rolls.length >0) ?  
            <Board 
              rolls={this.state.rolls} 
              rowsNum={this.state.rowsNum} 
              colsNum={this.state.colsNum}
            /> : <div></div>}
            </div>

      </Fragment>
    )
  }
}

export default App;
