import React, { useState, useEffect } from 'react';
import {Button, Typography, Box} from '@material-ui/core'
import useSound from 'use-sound';
import gongMp3 from  '../sounds/chinese-gong.mp3'
import dingMp3 from  '../sounds/ding.mp3'

const Timer = ({totalTime, countDown}) => {

  const [seconds, setSeconds] = useState(totalTime);
  const [isActive, setIsActive] = useState(false);

  function toggle() {
    setIsActive(!isActive);
  }

  function reset() {
    setSeconds(totalTime);
    setIsActive(false);
  }

  const [gong] = useSound(gongMp3);
  const [ding] = useSound(dingMp3);


useEffect(() => {
    let interval = null;
    if (isActive && seconds !== 0) {
            interval = setInterval(() => {
                setSeconds(seconds => seconds - 1)
            }, 1000);

            if (seconds > 0){
                if (seconds === totalTime - (totalTime/10)) {ding()}
                if (seconds < countDown) {ding()}
            }
            else
                { 
                gong()
                return () => clearInterval(interval);
            }
        }
        else if (!isActive) {clearInterval(interval);}

            //
}, [isActive, seconds])

  /*useEffect(() => {
        let interval = null;
        if (isActive) {
            interval = setInterval(() => {
                setSeconds(seconds => seconds - 1);

            }, 1000);
        } else if(!isActive && seconds !== 0) {
            clearInterval(interval);
        }
        if(seconds === 0) {gong()}
        return () => clearInterval(interval);
  }, [isActive, seconds]);*/

  return (
    <Box 
        padding="0px 5px"
        display="flexbox" 
        alignItems="center"
        justifyContent="space-between"
      >     


      <div>{totalTime} - {countDown}</div>
        <Button variant='contained' color="secondary" className={`${isActive ? 'active' : 'inactive'}`} onClick={toggle}>
          {isActive ? 'Pause' : 'Play'}
        </Button>
        <Button variant='contained' color="secondary"  onClick={reset}>
          Reset
        </Button>
        <Typography variant='h4'>
        {seconds}s
        </Typography>
    </Box>
  );
};

export default Timer;
