import React, { Component, useState } from 'react'
import dice from '../data'
import math from '../funcLibrary/math'

class Dice extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dice: dice,
            rolls: []
        }
        this.handleEvent = this.handleEvent.bind(this);
    }

    handleEvent(event, id) {
        event.stopPropagation();
        switch (event.type) {
            case 'click':
                console.log('clicked ', event.target, id);
                this.rollsDice()
                break
            default:
                console.log('unhandled ', event.type)
        }
    }

    rollsDice() { 
        //clona l'array dice in rolls 
        const rolls = [...dice] ; 
        rolls.map(die => {
            console.log(math)
            const roll = math.getRandomInt(0,5)
            console.log(roll)
            return [die[roll]]
        })
        this.setState({'rolls':rolls})
        console.log('rolls ', this.state.rolls)
    }
    componentDidMount() {

    }
    render() {
        console.log('rolls ', this.state.rolls)
        return (

            <div>
                <button key='1' onClick={(e, key) => this.handleEvent(e, key)}>
                    Roll dice
            </button>
                <div>
                    {
                    this.state.rolls.length !== 0 ? (this.state.rolls.map(die => {
                        return (
                            <div>
                                {die}
                            </div>
                        )
                    })) : <div></div> }
                </div>
            </div>
        )
    }
}

export default Dice
