import React, { Component, Fragment } from 'react';
import './App.css';
import Board from './components/board'
import dice from './data'
import PrimarySearchAppBar from './components/PrimarySearchAppBar'
import { Paper, Button, ThemeProvider } from '@material-ui/core'
import { withStyles } from '@material-ui/core/styles';
import theme from './theme';

//import <link rel="stylesheet" href="https://fonts.googleapis.com/css?family=Roboto:300,400,500,700&display=swap" />

const StyledPaper = withStyles({
  root: {
    background: '#efebe9', // pale brown
    color: '#3e2723', // dark brown
    height: '650px',
    padding: '0 30px',

  }
})(Paper);

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      dice: dice,
      rolls: [],
      rowsNum: 5,
      colsNum: 5
    }
    this.handleEvent = this.handleEvent.bind(this);
  }


  handleEvent(event, id) {
    event.stopPropagation();
    switch (event.type) {
      case 'click':
        console.log('clicked ', event.target, id);
        this.setState(() => ({ 'rolls': this.rollsDice() }))
        console.log('state rolls ', this.state.rolls)
        break
      default:
        console.log('unhandled ', event.type)
    }
  }

  rollsDice() {
    //clona l'array dice in rolls 
    let rolls = []
    rolls = dice.map(die => {
      const roll = Math.floor(Math.random() * dice[0].length)
      console.log('roll', roll)
      console.log('die number', die)
      console.log('die value', die[roll])
      // rolls.push(die[roll])
      return die[roll]
    })
    console.log('rolls', rolls)
    return rolls
  }

  render() {

    return (

      <ThemeProvider theme={theme}>
        <Fragment>
        <PrimarySearchAppBar/>
            <Button variant='contained' color="secondary" key={'rollDicesBtn'}
              onClick={(e, key) => this.handleEvent(e, key)}>
              Roll dice
          </Button>

          {(this.state.rolls.length > 0) ?

            <Board
              rolls={this.state.rolls}
              rowsNum={this.state.rowsNum}
              colsNum={this.state.colsNum}
            />
            : <div></div>}

        </Fragment>
      </ThemeProvider>
    )
  }
}

export default App;
