import React, { Component, Fragment } from 'react';
import './App.css';
import Board from './components/board'
import Dice from './components/dice'

//import <link rel="stylesheet" href="https://fonts.googleapis.com/css?family=Roboto:300,400,500,700&display=swap" />


class App extends Component{

  constructor(props) {
    super(props);
    this.state = {
        dice: dice,
        rolls: []
    }
    this.handleEvent = this.handleEvent.bind(this);
}

handleEvent(event, id) {
  event.stopPropagation();
  switch (event.type) {
      case 'click':
          console.log('clicked ', event.target, id);
          this.rollsDice()
          break
      default:
          console.log('unhandled ', event.type)
  }
}

rollsDice() { 
  //clona l'array dice in rolls 
  let rolls = []
  rolls = dice.map(die => {
      const roll = Math.floor( Math.random() * ( 6 - 1 + 1 ) ) + 1
      console.log('roll', roll)
      return [die[roll]]
  })
  this.setState({'rolls':rolls})
  console.log('rolls ', this.state.rolls)
}

  render() {
    return (
      <Fragment>
        <div>
            <button key='1' onClick={(e, key) => this.handleEvent(e, key)}>
                    Roll dice
            </button>
                <div>
                    {
                    this.state.rolls.length !== 0 ? (this.state.rolls.map((die,index) => {
                        return (
                          <Die key={index} roll={die}/>
                        )
                    })) : <div></div> }
                </div>
            </div>

      </Fragment>
    )
  }
}

export default App;
