import React, { Fragment } from 'react';
import Headers from './headers'
import Die from './die'
console.log('Board')

function Board({ rolls, rowsNum, colsNum }) {

    console.log(`printing board with ${rows} rows and ${cols} columns`)

    let content = []
    let rowRolls = []
    for (let roll in rolls) {
        for (let r = 1; r <= rowsNum; r++) {
            rowRolls.push(roll)
        }
    }
    content.push(<tr><Row rowRolls={rowRolls} /></tr>)
    return (
        <div>
            {content}
        </div>

    )
}


export default Board
