import React, { Component, Fragment, useState } from 'react';
import Board from './board'
import PrimarySearchAppBar from './myAppBar'
import { Button } from '@material-ui/core'

function handleEvent(event, id) {
    event.stopPropagation();
    switch (event.type) {
      case 'click':
        console.log('clicked ', event.target, id);
        this.setState(() => ({ 'rolls': this.rollsDice() }))
        console.log('state rolls ', state.rolls)
        break
      default:
        console.log('unhandled ', event.type)
    }
  }

function Ui(props) {
    const [state] = useState(props.state);
    console.log('state', state);


  return (  

<Fragment>
          <PrimarySearchAppBar />
            {(state.rolls.length > 0 && typeof(state.rolls)!== 'undefined' ) ?
              <Board
                rolls={state.rolls}
                rowsNum={state.rowsNum}
                colsNum={state.colsNum}
              />
              : <div></div>}

            <Button 
              style={{position: 'absolute', left: '50%',
                      transform: 'translate(-50%, -50%)'}} 
              variant='contained' color="secondary" key={'rollDicesBtn'}
              onClick={(e, key) => state.rolls = handleEvent(e, key)}>
              Roll dice
          </Button>
        </Fragment>
  )
}

export default Ui
