import React, { useEffect, useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import {GlobalState} from "../store/store"

const useStyles = makeStyles((theme) => ({
  appBar: {
    position: 'relative',
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1,
  },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

export default function FullScreenDialog() {

  const classes = useStyles();

  const [globalState, globalDispatch] = useContext(GlobalState);

  useEffect(() => {
    console.log('globalState ', globalState)
  });

  const handleClose = () => {
    globalDispatch({ type: "CLOSE_SETTINGS" })
  };

  return (
    <div>
      <Button variant="outlined" color="primary">
        Open full-screen dialog
      </Button>
      <Dialog open={globalState.isSettingsDialogOpen} onClose={handleClose} TransitionComponent={Transition}>
        <AppBar className={classes.appBar}>
          <Toolbar>
            <IconButton edge="start" color="inherit" onClick={handleClose} aria-label="close">
              <CloseIcon />
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              Settings
            </Typography>
            <Button autoFocus color="inherit" onClick={handleClose}>
              save
            </Button>
          </Toolbar>
        </AppBar>
        <List>
          {/*Object.keys(globalState).map(function(key)*/}

          <ListItem key={'1'} button>
            <ListItemText primary='Number of rows: ' secondary={globalState.rowsNum} onClick={() => globalDispatch({ type: "DEC_ROWSNUM" })} />
          </ListItem>
          <Divider />
          <ListItem key={'2'} button>
            <ListItemText primary='Number of columns: ' secondary={globalState.colsNum} onClick={() => globalDispatch({ type: "DEC_COLSNUM" })} />
          </ListItem>
          <Divider />
        </List>
      </Dialog>
    </div >
  )
}
