import React, { Component, Fragment, useState } from 'react';
import './App.css';
import Board from './components/board'
import dice from './data'
import PrimarySearchAppBar from './components/myAppBar'
import { Paper, Button, ThemeProvider } from '@material-ui/core'
import { withStyles } from '@material-ui/core/styles';
import theme from './theme';
import Ui from './components/ui'

//import <link rel="stylesheet" href="https://fonts.googleapis.com/css?family=Roboto:300,400,500,700&display=swap" />


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      dice: dice,
      rolls: [],
      rowsNum: 5,
      colsNum: 5
    }
    //this.setState(()=>({'rolls': this.rollsDice()}));
    this.handleEvent = this.handleEvent.bind(this);
  }

  componentDidUpdate(){
    this.setState(()=>({'rolls': this.rollsDice()}));
  }

  handleEvent(event, id) {
    console.log('parent handleEvent')
    //event.stopPropagation();
    switch (event.type) {
      case 'click':
        console.log('clicked ', event.target, id);
        this.setState(() => ({ 'rolls': this.rollsDice() }))
        console.log('state rolls in parent ', this.state.rolls)
        break
      default:
        console.log('unhandled ', event.type)
    }
  }

  rollsDice() {
    //clona l'array dice in rolls 
    let rolls = []
    rolls = dice.map(die => {
      const roll = Math.floor(Math.random() * dice[0].length)
      console.log('roll', roll)
      console.log('die number', die)
      console.log('die value', die[roll])
      // rolls.push(die[roll])
      return die[roll]
    })
    console.log('rolls', rolls)
    return rolls
  }

  render() {

    return (
      <ThemeProvider theme={theme}>
        <Ui state={this.state} handler={this.handleEvent}/>
      </ThemeProvider>
    )
  }
}

export default App;
