import React, { Component, Fragment } from 'react';
import './App.css';
import Board from './components/board'
import dice from './data'
import PrimarySearchAppBar from './components/myAppBar'
import { Paper, Button, ThemeProvider } from '@material-ui/core'
import { withStyles } from '@material-ui/core/styles';
import theme from './theme';


function Ui() {

<Fragment>
          <PrimarySearchAppBar />
            {(this.state.rolls.length > 0) ?
              <Board
                rolls={this.state.rolls}
                rowsNum={this.state.rowsNum}
                colsNum={this.state.colsNum}
              />
              : <div></div>}

            <Button 
              style={{position: 'absolute', left: '50%',
                      transform: 'translate(-50%, -50%)'}} 
              variant='contained' color="secondary" key={'rollDicesBtn'}
              onClick={(e, key) => this.handleEvent(e, key)}>
              Roll dice
          </Button>
        </Fragment>
}

export default Ui
