import React, { Component, Fragment } from 'react';
import './App.css';
import Board from './components/board'
import Die from './components/die'
import dice from './data'
import {Paper, Button} from '@material-ui/core'
import { withStyles } from '@material-ui/core/styles';

//import <link rel="stylesheet" href="https://fonts.googleapis.com/css?family=Roboto:300,400,500,700&display=swap" />

const styles = {
  root: {
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    borderRadius: 3,
    border: 0,
    color: 'white',
    height: 48,
    padding: '0 30px',
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
  },
};

class App extends Component{

  constructor(props) {
    super(props);
    this.state = {
        dice: dice,
        rolls: [],
        rowsNum: 5,
        colsNum: 5
    }
    this.handleEvent = this.handleEvent.bind(this);
}

handleEvent(event, id) {
  event.stopPropagation();
  switch (event.type) {
      case 'click':
          console.log('clicked ', event.target, id);
          this.setState(()=> ({'rolls': this.rollsDice()}))
          console.log('state rolls ', this.state.rolls)
          break
      default:
          console.log('unhandled ', event.type)
  }
}

rollsDice() { 
  //clona l'array dice in rolls 
  let rolls = []
  dice.map(die => {
      const roll = Math.floor( Math.random() * 6 )
        console.log('roll', roll)
        console.log('die number', die)
        console.log('die value',die[roll])
      rolls.push(die[roll])
  })
  console.log('rolls', rolls)
  return rolls
}

  render() {
    return (
      <Fragment>
        <Paper>
            <Button variant="contained"  color="secondary" key='1'
            onClick={(e, key) => this.handleEvent(e, key)}>
                    Roll dice
            </Button>
            {(this.state.rolls.length >0) ? 
            <table>
              <Board 
                rolls={this.state.rolls} 
                rowsNum={this.state.rowsNum} 
                colsNum={this.state.colsNum}
              /> 
            </table> : <div></div>}
        </Paper>

      </Fragment>
    )
  }
}

export default App;
