import React, { useEffect, useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import GlobalState from "../store/globalState"
import LocalSettingsState from "../store/localSettingsState"
import LocalSettingsReducer from "./store/localSettingReducer"
import Slider from '@material-ui/core/Slider';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel'
import Input from '@material-ui/core/Input'
import FormHelperText from '@material-ui/core/FormHelperText'

const useStyles = makeStyles((theme) => ({
  appBar: {
    position: 'relative',
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1,
  },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

const initialLocalSettingsState = {
  rowsNum: globalState.rowsNum,
  colsNum: globalState.colsNum,
  minRows: globalState.minRows,
  minCols: globalState.minCols,
  maxRows: globalState.maxRows,
  maxCols: globalState.maxCols,
};

export default function FullScreenDialog(props) {
  console.log('props ', props)

  const classes = useStyles();
  const [localSettingsState, localSettingsDispatch] = useReducer(reducer,initialLocalSettingsState)
  const { globalState, globalDispatch } = useContext(GlobalState);
  //const { localSettingsState, localSettingsDispatch } = useContext(LocalSettingsState);
  const [settingsDialogVisibility, setSettingsDialogVisibility] = React.useState(true)

  useEffect(() => {

  }, []);

  const valuetext = (value) => {
    return `${value}`;
  }

  const changeRowsNum = (e, value) => {
    if (value * globalState.colsNum > 25) {
      alert('It is not possible set more than 25 dices ')
      return 
    }

    /*globalDispatch({
      type: "CHANGE_ROWSNUM",
      payload: value
    })*/
  }

  const changeColsNum = (e, value) => {
    if (value * globalState.rowsNum > 25) {
      alert('It is not possible set more than 25 dices')
      return 
    }
    /*globalDispatch({
      type: "CHANGE_COLSNUM",
      payload: value
    })*/
  }

  const saveChanges = (e, value) => {
    alert('Changes is being saved')
    globalDispatch({ type: "CLOSE_SETTINGS" })
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('form submitted rowsNum',document.getElementById('settingsForm').slideRowsNum.value)

    globalDispatch({
      type: "CHANGE_ROWSNUM",
      payload: document.getElementById('settingsForm')
    })

    globalDispatch({
      type: "CHANGE_COLSNUM",
      payload: document.getElementById('settingsForm').slideColsNum.value
    })

    globalDispatch({ type: "CLOSE_SETTINGS" })
  }

  return (
    <div>
      <Button variant="outlined" color="primary">
        Open full-screen dialog
      </Button>
      <Dialog  
              open={settingsDialogVisibility} 
              onClose={props.onClose} 
              TransitionComponent={Transition}
      >
        <AppBar className={classes.appBar}>
          <Toolbar>
            <IconButton edge="start" color="inherit" onClick={props.onClose} aria-label="close">
              <CloseIcon />
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              Settings
            </Typography>

          </Toolbar>
        </AppBar>
        <form  key='settingForm' onSubmit={handleSubmit}>
          <FormControl >

            <List>
              {/*Object.keys(globalState).map(function(key)*/}

              <ListItem key={'1'} button>
                <ListItemText primary='Number of rows: ' secondary={globalState.rowsNum} onClick={() => globalDispatch({ type: "DEC_ROWSNUM" })} />

                <Slider
                  key="slideRowsNum"
                  defaultValue={globalState.rowsNum}
                  getAriaValueText={valuetext}

                  onChange={changeRowsNum}
                  aria-labelledby="discrete-slider-small-steps"
                  step={1}
                  marks
                  min={globalState.minRows}
                  max={globalState.maxRows}
                  valueLabelDisplay="auto"
                />

              </ListItem>
              <Divider />
              <ListItem key={'2'} button>
                <ListItemText primary='Number of columns: ' secondary={globalState.colsNum} onClick={() => globalDispatch({ type: "DEC_COLSNUM" })} />
                <Slider
                  key="slideColsNum"
                  defaultValue={globalState.rowsNum}
                  getAriaValueText={valuetext}
                  onChange={changeColsNum}
                  aria-labelledby="discrete-slider-small-steps"
                  step={1}
                  marks
                  min={globalState.minCols}
                  max={globalState.maxCols}
                  valueLabelDisplay="auto"
                />
              </ListItem>
              <Divider />
            </List>
            <Button key="saveSettings" autoFocus color="inherit" type="submit">
              Save
            </Button>
          </FormControl>
        </form>
      </Dialog>
    </div >
  )
}
