const reducer = (state, action) => {
    switch (action.type) {
        case "OPEN_SETTINGS":
            return {
                isSettingsDialogOpen: true
            }
        case "CLOSE_SETTINGS":
            console.log('close_settings')
            return {
                isSettingsDialogOpen: false
            }
        case "CHANGE_ROWSNUM":
            return {
                rowsNum: 4
            }
        case "CHANGE_COLSNUM":
            return {
                rowsNum: 5
            }
        case "INC_MATCH":
            return {
                match: 1
            }
        case "SET_TOTALTIME":
            return {
                totalTime: 10
            }
        case "INC_COUNTDOWN":
            return {
                countDown: 1
            }
        case "DEC_COUNTDOWN":
            return {
                countDown: 1
            }
        default:
            {
                return state
            }
    }

}

export default reducer;
