import React, { Component, Fragment, useState } from 'react';
import Board from './board'
import PrimarySearchAppBar from './myAppBar'
import { Button, Card, Typography } from '@material-ui/core'


function Ui({parentState, handleEvent}) {
    const rolls = parentState.rolls;
    const rollsProgressive = parentState.rollsProgressive;
    console.log('children rolls ', rolls);

  return (  

        <Fragment>
          <PrimarySearchAppBar />
            {(rolls.length > 0 && typeof(rolls)!== 'undefined' ) ?
              <Board
                rolls={rolls}
                rowsNum={parentState.rowsNum}
                colsNum={parentState.colsNum}
              />
              : <div></div>}
              <Card style={{position: 'absolute', left: '50%',
                      transform: 'translate(-50%, -50%)'}} >
                Partita <Typography 
                        style={{'text-align': 'center'}}
                        variant='h3'>
                            {rollsProgressive} 
                    </Typography>
              </Card>
            <Button 
              style={{position: 'absolute', left: '50%'
                     }} 
              variant='contained' color="secondary" key={'rollDicesBtn'}
              onClick={(e,key) => handleEvent(e,key)}>
                Roll dice
          </Button>
        </Fragment>
  )
}

export default Ui
