import React, { Fragment } from 'react';
import Headers from './headers'
import Row from './row'
console.log('Board')

function Board({ rolls, colsNum }) {

    console.log(`printing board with ${rolls.length} rolls in ${colsNum} columns`)
    console.log('rolls ', rolls)
    console.log('columns',colsNum)
    let rows = []
    let rowRolls = []
    for (let [index, roll] of rolls.entries()) {
            rowRolls.push({
                x:(colsNum/index+1)-colsNum,
                y:index*colsNum/colsNum,
                content: rolls[roll]
            })

            if(rowRolls.length === colsNum) 
            {   
                console.log(`pushing rowRolls row_${(colsNum/index+1)-colsNum} into the board`)
                rows.push(<tr key={`row_${(colsNum/index+1)-colsNum}`.toString()}><Row key={`row_${(colsNum/index+1)-colsNum}`.toString()} id={(colsNum/index+1)-colsNum} rowRolls={rowRolls}/></tr>)
                rowRolls = []
            } 
        }
     return (rows)
}
export default Board
