import React, { Component, Fragment, useState, useContext} from 'react';
import Board from './board'
import Timer from './timer'
import PrimarySearchAppBar from './myAppBar'
import { Button, Card, Typography, Box, InputBase } from '@material-ui/core'
//import {Context} from "../store/store"
import GlobalStateContext from "../App"
import DispatchStateContext from "../App"


const useGlobalState = () => [
    React.useContext(GlobalStateContext),
    React.useContext(DispatchStateContext)
  ];

const Ui = React.memo ((parentState, handleEvent, onChangeRowsNum) => {
    const [globalState, globalDispatch] = useGlobalState();
    console.log('globalState on ui', globalState)
    const rolls = parentState.rolls;
    const timer = globalState.timer;
    const match = globalState.match;
    console.log('children rolls ', rolls);

    return (

        <Fragment>
            <PrimarySearchAppBar gpState={parentState} onChangeRowsNum={onChangeRowsNum}/>   
            <Box
                width='350px'
                padding="10px 5px"
                border='5px'
                display="block"
                alignItems="center"
                justifyContent="center"

            >
                <Box 
                    padding="0px 5px"
                    display="flex"
                    alignItems="center"
                    justifyContent="space-between"
                >
                    <Typography
                        variant='h4'>
                        Match {match}
                    </Typography>

                    <Button
                        variant='contained' color="secondary" key={'rollDicesBtn'}
                        onClick={(e, key) => handleEvent(e, key)}>
                        Roll dice
                    </Button>
                </Box>


                {(rolls.length > 0 && typeof (rolls) !== 'undefined') ?
                <Board
                    rolls={rolls}
                    rowsNum={globalState.rowsNum}
                    colsNum={globalState.colsNum}
                />
                : <div></div>}

                <Timer totalTime={globalState.totalTime} countDown={globalState.countDown}/>
            </Box>
        </Fragment>
    )
})

export default Ui
