import React, { Component, Fragment, useState } from 'react';
import Board from './board'
import PrimarySearchAppBar from './myAppBar'
import { Button } from '@material-ui/core'


function Ui(props) {
    const [state] = useState(props.state);

  return (  

<Fragment>
          <PrimarySearchAppBar />
            {(this.state.rolls.length > 0) ?
              <Board
                rolls={this.state.rolls}
                rowsNum={this.state.rowsNum}
                colsNum={this.state.colsNum}
              />
              : <div></div>}

            <Button 
              style={{position: 'absolute', left: '50%',
                      transform: 'translate(-50%, -50%)'}} 
              variant='contained' color="secondary" key={'rollDicesBtn'}
              onClick={(e, key) => this.handleEvent(e, key)}>
              Roll dice
          </Button>
        </Fragment>
  )
}

export default Ui
