import React, { Fragment } from 'react';
import Headers from './headers'
import Row from './row'
console.log('Board')

function Board({ rolls, colsNum }) {

    console.log(`printing board with ${rolls.length} rolls in ${colsNum} columns`)
    console.log('rolls ', rolls)
    console.log('columns',colsNum)
    let rows = []
    let rowRolls = []
    for (let [index, roll] of rolls.entries()) {
        console.log('index ' + index)
            rowRolls.push({
                x:(index+1)/colsNum,
                y:index+1,
                content: rolls[roll]
            })

            if(rowRolls.length === colsNum) 
            {   
                console.log(`pushing rowRolls row_${(index+1)/colsNum} into the board`)
                rows.push(<tr key={`row_${index+1}`.toString()}><Row key={`row_${index+1}`.toString()} id={index+1} rowRolls={rowRolls}/></tr>)
                rowRolls = []
            } 
        }
     return (rows)
}
export default Board
