import React, { Component, Fragment, useState, useContext, useGlobal} from 'react';
import Board from './board'
import Timer from './timer'
import PrimarySearchAppBar from './myAppBar'
import { Button, Card, Typography, Box, InputBase } from '@material-ui/core'
import dice from "./dice"
import { Global } from '@emotion/core';
import GlobalState from "../store/globalState"


const Ui = (() => {

    const [globalState, globalDispatch] = useContext(GlobalState);

    const timer = globalState.timer;
    const match = globalState.match;
    const rolls = globalState.rolls;

    console.log('children rolls ', rolls);

    const makeRollsDice = () => {
        console.log('makeRollsDice')
        //console.log('appState', appState)
        let rowsNum = 5
        let colsNum = 5
        if (typeof(globalState)!== 'undefined'){
           rowsNum = state.rowsNum
           colsNum = state.colsNum
        }

        console.log('state rows and cols', rowsNum, colsNum)
        console.log('state type ', typeof (appState))
        //console.log('state ', appState)
        console.log(rowsNum, colsNum)
        let rolls = []
        //clone whole diceStack
        let diceStack = [...dice]
        while (diceStack.length > dice.length - (rowsNum * colsNum)) {
          let dieNumber = Math.floor(Math.random() * diceStack.length);
          let dieFace = Math.floor(Math.random() * 6);
          rolls.push(diceStack[dieNumber][dieFace])
          diceStack.splice(dieNumber, 1)
          console.log('diceStack ', diceStack.length)
          console.log('soglia ', dice.length - (rowsNum * colsNum))
        }
        console.log('dice number', rolls.length)
        console.log('rolls', rolls)
        return rolls
      }


      const handleEvent = (event, rollsDice) => {
        switch (event.type) {
          case 'click':
            console.log('clicked ', event.target);
            setState({type:"INC_MATCH"})
              console.log('appState setted')
            break
          default:
            console.log('unhandled ', event.type)
      };
    }

    return (

        <Fragment>
            <PrimarySearchAppBar/>   
            <Box
                width='350px'
                padding="10px 5px"
                border='5px'
                display="block"
                alignItems="center"
                justifyContent="center"

            >
                <Box 
                    padding="0px 5px"
                    display="flex"
                    alignItems="center"
                    justifyContent="space-between"
                >
                    <Typography
                        variant='h4'>
                        Match {state.match}
                    </Typography>

                    <Button
                        variant='contained' color="secondary" key={'rollDicesBtn'}
                        onClick={(e, key) => handleEvent(e, key)}>
                        Roll dice
                    </Button>
                </Box>

                {console.log(state.rolls) (state.rolls.length > 0 && typeof (state.rolls) !== 'undefined') ?
                <Board
                    rolls={state.rolls}
                    rowsNum={state.rowsNum}
                    colsNum={state.colsNum}
                />
                : <div></div>}

                <Timer totalTime={state.totalTime} countDown={state.countDown}/>
            </Box>
        </Fragment>
    )})

export default Ui
