import React, { Component, Fragment, useState } from 'react';
import './App.css';
import Board from './components/board'
import dice from './data'
import PrimarySearchAppBar from './components/myAppBar'
import { Paper, Button, ThemeProvider, Typography, InputBase } from '@material-ui/core'
import { withStyles } from '@material-ui/core/styles';
import theme from './theme';
import Ui from './components/ui'

//import <link rel="stylesheet" href="https://fonts.googleapis.com/css?family=Roboto:300,400,500,700&display=swap" />

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      dice: dice,
      rowsNum: 5,
      colsNum: 5,
      rolls: this.rollsDice(),
      match: 0,
      totalTime: 10,
      countDown: 2
    }
    this.handleEvent = this.handleEvent.bind(this);
    this.handleRowsNum= this.handleRowsNum.bind(this)

  }
  renderRowsNum(){
    const isRowsNumOpen = Boolean(this.state.rowsNum === 5 ? false : true);
    const windowId = 'change-parameter';
    return(
    <div>
    <form
      rowsNum={this.state.rowsNum}
      //anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
      id={windowId}
      keepMounted
      //transformOrigin={{ vertical: 'top', horizontal: 'right' }}
      open={isRowsNumOpen}

      >
      <div >
              <InputBase
                type="text" 
                placeholder="Rows"
                /*classes={{
                  root: classes.inputRoot,
                  input: classes.inputInput,
                }}
                */
                onChange={handleChangeRowsNum} 
                />
      </div>
    </form>
    <Typography>{this.state.rowsNum}</Typography>
    </div>

  )};

    handleEvent(event, id) { 
      console.log('parent handleEvent')
      event.stopPropagation();
      switch (event.type) {
        case 'click':
          console.log('clicked ', event.target, id);
          this.setState(() => (
            { rolls: this.rollsDice(this.state.rowsNum)}))
          break
        default:
          console.log('unhandled ', event.type)
      }
    }
    changeRowsNum(value){
      this.setState({ 
        rowsNum: value,
        colsNum: value
      }) 
    }

    handleRowsNum({ target: { value } }){
      console.log('changing rows number')
      this.changeRowsNum(value);
    }

    rollsDice() {
      console.log('rollsDice')
      let rowsNum = 0
      if (typeof(this.state === 'undefined')){
        rowsNum = 5
      }
      else{
        rowsNum = this.state.rowsNum
      }
      let rolls = []
      //clone whole diceStack
      let diceStack = [...dice]
      while (diceStack.length > (dice.length-(rowsNum*rowsNum))){
        let dieNumber = Math.floor(Math.random() * diceStack.length); 
        let dieFace = Math.floor(Math.random() * rowsNum); 
        rolls.push(diceStack[dieNumber][dieFace])
        diceStack.splice(dieNumber,1)
      }

      console.log('last rolls', rolls)
      this.setState(() => (
        { 
          'match': this.state.match+1 }))
      return rolls
    }




  render() {
    return (
      <ThemeProvider theme={theme}>
          <InputBase
                type="text" 
                placeholder="Rows"
                onChange={handleChangeRowsNum} 
                />
               {renderRowsNum}
        <Ui parentState={this.state} 
          handleEvent={this.handleEvent} 
          onChangeRowsNum={this.handleRowsNum}/>
      </ThemeProvider>
    )
  }
}

export default App;
