const reducer = (state, action) => {
    switch (action.type) {

        case "OPEN_SETTINGS":
            console.log('global state on OPEN_SETTINGS  ', state)
            return {
                ...state,
                isSettingsDialogOpen: true
            }
        case "CLOSE_SETTINGS":
            console.log('close_settings')
            return {
                ...state,
                isSettingsDialogOpen: false
            }
        case "DEC_ROWSNUM":

            return {
                ...state,
                rowsNum: [...state].rowsNum-1
            }
        case "CHANGE_COLSNUM":
            return {
                ...state,
                rowsNum: 5
            }
        case "INC_MATCH":
            return {
                ...state,
                match: 1
            }
        case "SET_TOTALTIME":
            return {
                ...state,
                totalTime: 10
            }
        case "INC_COUNTDOWN":
            return {
                ...state,
                countDown: 1
            }
        case "DEC_COUNTDOWN":
            return {
                ...state,
                countDown: 1
            }
        default:
            {
                return state
            }
    }

}

export default reducer;
