import React, { Fragment } from 'react';
import Headers from './headers'
import Row from './row'
console.log('Board')

function Board({ rolls, colsNum }) {

    console.log(`printing board with ${rolls.length} rolls in ${colsNum} columns`)
    console.log('rolls ', rolls)
    let rows = []
    let rowRolls = []
    let rowIndex = 0
    for (let roll in rolls) {
            rowRolls.push(rolls[roll])

            if(rowRolls.length === colsNum) 
            {
                rowIndex = rowIndex+1

                console.log(`pushing rowRolls ${rowIndex} row_${rowIndex+1} into the board`)
                rows.push(<tr key={`row_${rowIndex+1}`.toString()}><Row id={`row_${rowIndex+1}`.toString()}rowRolls={rowRolls}/></tr>)
                rowRolls = []
            } 
        }
     return (rows)
}
export default Board
