import React, { Fragment, useContext } from 'react';
import Headers from './headers'
import Row from './row'
import {Typography, Card} from '@material-ui/core';
import 'fontsource-roboto';
import { makeStyles } from '@material-ui/core/styles';
import { Context } from "../store/globalState"

const useStyles = makeStyles(theme =>({
    board:{
        width: '350px',
        margin: '30px 0px',
        border: '5px',
        display: 'flex',
        alignItems: "center",
        justifyContent: "center"

        //color: theme.palette.openTitle,
    }
}))

function Board({ originalRolls, rowsNum, colsNum }) {

    const classes = useStyles()

    console.log(`printing board with ${originalRolls.length} rolls in ${colsNum} columns`)
    console.log('rolls length', originalRolls.length)
    console.log('columns',colsNum)

    let rows = []
    let rowRolls = []
    let rowCounter = 0
    const dicesToRemove = 25-(colsNum*colsNum)
    const rolls = originalRolls.splice(0,dicesToRemove)
    for (let [index,value] of rolls.entries()) {
        console.log(rowCounter, index+1, value
         )
            rowRolls.push({
                i:index+1,
                x:rowCounter+1,
                y:(index+1+colsNum)-(colsNum*(rowCounter+1)),
                content: value
            })

             if(rowRolls.length === colsNum) 
            {   
                console.log(`pushing rowRolls row_${rowCounter} into the board `,rowRolls)
                rows.push(<tr key={rowCounter.toString()}><Row key={rowCounter.toString()} id={rowCounter+1} rowRolls={rowRolls}/></tr>)
                rowRolls = []
                rowCounter++
            } 

        }
        console.log('printing rows',rows)
        return (

            <table className={classes.board}>
                    <tbody> 
                        {rows} 
                    </tbody>
                </table>
        )

}
export default Board
