    let content = []
    let rowRolls = []
    for (let roll in rolls) {
            rowRolls.push(roll)
            if(rowRolls.length = colsNum) 
            {
                rowRolls = []
                return <tr><Row rowRolls={rowRolls} /></tr>
            } 
        }
}


export default Board
