import React, { Component, Fragment, useState } from 'react';
import Board from './board'
import Timer from './timer'
import PrimarySearchAppBar from './myAppBar'
import { Button, Card, Typography, Box, InputBase } from '@material-ui/core'


function Ui({ parentState, handleEvent, onChangeRowsNum }) {
    const rolls = parentState.rolls;
    const timer = parentState.timer;
    const match = parentState.match;
    console.log('children rolls ', rolls);


  const handleChangeRowsNum = (event) => {
    onChangeRowsNum(event)
  }
  const windowId = 'change-parameter';

  const renderRowsNum = (
    <div>
    <form
      rowsNum={parentState.rowsNum}
      //anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
      id={windowId}
      keepMounted
      //transformOrigin={{ vertical: 'top', horizontal: 'right' }}
      open={isRowsNumOpen}

      >
      <div className={classes.search}>
              <InputBase
                type="text" 
                placeholder="Rows"
                /*classes={{
                  root: classes.inputRoot,
                  input: classes.inputInput,
                }}
                */
                onChange={handleChangeRowsNum} 
                />
      </div>
    </form>
    <Typography>{gpState.rowsNum}</Typography>
    </div>

  );

    return (

        <Fragment>
            <PrimarySearchAppBar gpState={parentState} onChangeRowsNum={onChangeRowsNum}/>   
            <InputBase
                type="text" 
                placeholder="Rows"
                onChange={handleChangeRowsNum} 
                />
               {renderRowsNum}
            <Box
                width='350px'
                padding="10px 5px"
                border='5px'
                display="block"
                alignItems="center"
                justifyContent="center"

            >
                <Box 
                    padding="0px 5px"
                    display="flex"
                    alignItems="center"
                    justifyContent="space-between"
                >
                    <Typography
                        variant='h4'>
                        Match {match}
                    </Typography>

                    <Button
                        variant='contained' color="secondary" key={'rollDicesBtn'}
                        onClick={(e, key) => handleEvent(e, key)}>
                        Roll dice
                    </Button>
                </Box>


                {(rolls.length > 0 && typeof (rolls) !== 'undefined') ?
                <Board
                    rolls={rolls}
                    rowsNum={parentState.rowsNum}
                    colsNum={parentState.colsNum}
                />
                : <div></div>}

                <Timer totalTime={parentState.totalTime} countDown={parentState.countDown}/>
            </Box>
        </Fragment>
    )
}

export default Ui
