import React, { useContext, useEffect, useState } from 'react';
import './App.css';
import Board from './components/board'
import dice from './data'
import PrimarySearchAppBar from './components/myAppBar'
import { Paper, Button, ThemeProvider, Typography, InputBase } from '@material-ui/core'
import { withStyles } from '@material-ui/core/styles';
import theme from './theme';
import Ui from './components/ui'
import Store from "./store/store";
import {Context} from "./store/store"

//import <link rel="stylesheet" href="https://fonts.googleapis.com/css?family=Roboto:300,400,500,700&display=swap" />


const App = () => {

  const initialState = {
    dice: dice,
    rowsNum:5,
    colsNum:5,
    rolls:[],
    //match:0,
    totalTime:10,
    countDown:2
  }

  const initialGlobalState = {
    isSettingsDialogOpen: false,
    rowsNum: 5,
    colsNum: 5,
    match: 0,
    totalTime: 10,
    countDown: 2
};

  const [appState, setAppState] = React.useState(initialState);
  const globalStateContext = React.createContext(initialGlobalState);

  console.log('globalState in main app', globalState)
  console.log('globalDispatch', globalDispatch)


  const rollsDice = () => {
    console.log('rollsDice')
    console.log('appState', appState)

      const rowsNum = appState.rowsNum
      const colsNum = appState.colsNum
      console.log('state rows and cols', rowsNum, colsNum)
    console.log('state type ', typeof (appState))
    console.log('state ', appState)
    console.log(rowsNum, colsNum)
    let rolls = []
    //clone whole diceStack
    let diceStack = [...dice]
    while (diceStack.length > dice.length - (rowsNum * colsNum)) {
      let dieNumber = Math.floor(Math.random() * diceStack.length);
      let dieFace = Math.floor(Math.random() * 6);
      rolls.push(diceStack[dieNumber][dieFace])
      diceStack.splice(dieNumber, 1)
      console.log('diceStack ', diceStack.length)
      console.log('soglia ', dice.length - (rowsNum * colsNum))
    }
    console.log('dice number', rolls.length)
    console.log('rolls', rolls)
    return rolls
  }

  useEffect(() => {
    console.log('appState', appState)
  });

  const changeRowsNum = (value) => {
    setAppState({
      rowsNum: appState.rowsNum + value,
      colsNum: appState.colsNum + value,
    })
    console.log('new rowsNum', appState.rowsNum)
    console.log('new colsNum', appState.colsNum)
  }

  const decrementRows = () => {
    console.log('decrement rows number')
    changeRowsNum(-1);
  }

    const handleEvent = (event) => {
      switch (event.type) {
        case 'click':
          console.log('clicked ', event.target);
          //globalDispatch({type:"INC_MATCH"})
          setAppState(() => (
            {
              ...appState,
              rolls: rollsDice(),
              //match: appState.match + 1 
            }))
            globalDispatch({type:"INC_MATCH"})
            console.log('appState setted')
          break
        default:
          console.log('unhandled ', event.type)
    };
  }



    return (
      <Store>
        <ThemeProvider theme={theme}>
          <Ui parentState = {appState} handleEvent={handleEvent}
          />
        </ThemeProvider>
      </Store>
    )
  }

export default App;
