      console.log(tile)
      return (  
        <GridListTile key={tile} cols={3} spacing={1}>
            <Dice row={2}col={3} tile={tile}/>
        </GridListTile>
        )}
    )}
</GridList>
</MuiThemeProvider>
)
      }


export default Board
