import React, { Fragment } from 'react';
import Headers from './headers'
import Row from './row'
console.log('Board')

function Board({ rolls, colsNum }) {

    console.log(`printing board with ${rolls.length} rolls in ${colsNum} columns`)
    console.log('rolls ', rolls)
    let content = []
    let rowRolls = []
    for (let roll in rolls) {
            rowRolls.push(rolls[roll])
            console.log('rowRolls ', rowRolls)
            console.log('rowRolls length', rowRolls.length)
            if(rowRolls.length == colsNum) 
            {
                content.push(<tr><Row rowRolls={rowRolls}/></tr>)
                rowRolls = []
            } 
        }
    console.log('content Board ', content)
    console.log('content row 1',content[0].props.children.props.rowRolls)
    return (
        {content}
    )
}


export default Board
