        console.log('rollsDice')
        console.log('appState', appState)
        let rowsNum = 5
        let colsNum = 5
        if (typeof(appState)!== 'undefined'){
           rowsNum = appState.rowsNum
           colsNum = appState.colsNum
        }

        console.log('state rows and cols', rowsNum, colsNum)
        console.log('state type ', typeof (appState))
        console.log('state ', appState)
        console.log(rowsNum, colsNum)
        let rolls = []
        //clone whole diceStack
        let diceStack = [...dice]
        while (diceStack.length > dice.length - (rowsNum * colsNum)) {
          let dieNumber = Math.floor(Math.random() * diceStack.length);
          let dieFace = Math.floor(Math.random() * 6);
          rolls.push(diceStack[dieNumber][dieFace])
          diceStack.splice(dieNumber, 1)
          console.log('diceStack ', diceStack.length)
          console.log('soglia ', dice.length - (rowsNum * colsNum))
        }
        console.log('dice number', rolls.length)
        console.log('rolls', rolls)
        return rolls
      }

    return (

        <Fragment>
            <PrimarySearchAppBar gpState={appState} onChangeRowsNum={onChangeRowsNum}/>   
            <Box
                width='350px'
                padding="10px 5px"
                border='5px'
                display="block"
                alignItems="center"
                justifyContent="center"

            >
                <Box 
                    padding="0px 5px"
                    display="flex"
                    alignItems="center"
                    justifyContent="space-between"
                >
                    <Typography
                        variant='h4'>
                        Match {match}
                    </Typography>

                    <Button
                        variant='contained' color="secondary" key={'rollDicesBtn'}
                        onClick={(e, key) => handleEvent(e, key)}>
                        Roll dice
                    </Button>
                </Box>

                {(rolls.length > 0 && typeof (rolls) !== 'undefined') ?
                <Board
                    rolls={rolls}
                    rowsNum={globalState.rowsNum}
                    colsNum={globalState.colsNum}
                />
                : <div></div>}

                <Timer totalTime={globalState.totalTime} countDown={globalState.countDown}/>
            </Box>
        </Fragment>
    )
    })
export default Ui
