import React, { Fragment } from 'react';
import Headers from './headers'
import Row from './row'
console.log('Board')

function Board({ rolls, colsNum }) {

    console.log(`printing board with ${rolls.length} rolls`)
    console.log('rolls ', rolls)
    let content = []
    let rowRolls = []
    for (let roll in rolls) {
            rowRolls.push(roll)
            if(rowRolls.length = colsNum) 
            {
                rowRolls = []
                content.push(<tr><Row rowRolls={rowRolls} /></tr>)
            } 
        }
    console.log('content Board ', rowRolls)
    return (
        {content}
    )
}


export default Board
