const localSettingsReducer = (state, action) => {
    switch (action.type) {

        case "CHANGE_ROWSNUM":

        return {
            ...state,
            rowsNum: action.payload
        }
        case "CHANGE_COLSNUM":

        return {
            ...state,
            colsNum: action.payload
        }


        default:
            {
                return state
            }
    }

}

export default localSettingsReducer;
